var insertIncident = function(incidentData, user_id, location, isPrimaryLocation, state) {

    // Get the user's record -- we need the name later
    var gr_user = new GlideRecord('sys_user');
    gr_user.get('sys_id', user_id);

	var msg = gr_user.getValue('user_name') + "/" + location + "/" + isPrimaryLocation + "/" + state;

    var gr = new GlideRecord("x_42999_store_inc_incident");
    gr.initialize();

    // populate all the fields from the boilerplate incident data...
    for (var p in incidentData) {
        if (incidentData.hasOwnProperty(p)) {
            gr.setValue(p, incidentData[p]);
        }
    }

    // ...but override these properties with the values of location, state
    //    and update the description
    gr.setValue('u_author', user_id);
    gr.setValue('u_state', state);
    gr.setValue('u_event_location', location);

    //var desc = gr.getValue('u_description');
    var desc = "State: " + state;
    desc += '/location: ' + (isPrimaryLocation ? "primary":"alternate");
    desc += '/Author: ' + gr_user.getValue('user_name') + ".";
    gr.setValue('u_description', desc);

    gr.update();

    if (incidentData.suspect) {
        for (var suspect_index = 0; suspect_index < incidentData.suspect.length; suspect_index++) {
            var suspect_row = incidentData.suspect[suspect_index];
            var gr_suspect = new GlideRecord("x_42999_store_inc_suspect");
            gr_suspect.initialize();
            for (var suspect_prop in suspect_row) {
                if (suspect_row.hasOwnProperty(suspect_prop)) {
                    gr_suspect.setValue(suspect_prop, suspect_row[suspect_prop]);
                }
            }
            gr_suspect.setValue('u_parent', gr.sys_id);
            gr_suspect.update();
        }
    }

    if (incidentData.witness) {
        for (var witness_index = 0; witness_index < incidentData.witness.length; witness_index++) {
            var witness_row = incidentData.witness[witness_index];
            var gr_witness = new GlideRecord("x_42999_store_inc_witness");
            gr_witness.initialize();
            for (var witness_prop in witness_row) {
                if (witness_row.hasOwnProperty(witness_prop)) {
                    gr_witness.setValue(witness_prop, witness_row[witness_prop]);
                }
            }
            gr_witness.setValue('u_parent', gr.sys_id);
            gr_witness.update();
        }
    }

    if (incidentData.police) {
        for (var police_index = 0; police_index < incidentData.police.length; police_index++) {
            var police_row = incidentData.police[police_index];
            var gr_police = new GlideRecord("x_42999_store_inc_police_response");
            gr_police.initialize();
            for (var police_prop in police_row) {
                if (police_row.hasOwnProperty(police_prop)) {
                    gr_police.setValue(police_prop, police_row[police_prop]);
                }
            }
            gr_police.setValue('u_parent', gr.sys_id);
            gr_police.update();
        }
    }

    if (incidentData.sku) {
        for (var sku_index = 0; sku_index < incidentData.sku.length; sku_index++) {
            var sku_row = incidentData.sku[sku_index];
            var gr_sku = new GlideRecord("x_42999_store_inc_sku");
            gr_sku.initialize();
            for (var prop in sku_row) {
                if (sku_row.hasOwnProperty(prop)) {
                    gr_sku.setValue(prop, sku_row[prop]);
                }
            }
            gr_sku.setValue('u_parent', gr.sys_id);
            gr_sku.update();
        }
    }

};

// Iterate each INCIDENT
for (var inc_index = 0; inc_index < incidents.length; inc_index++) {

    var incidentData = incidents[inc_index];

    // iterate each STATE
    for (var state_index = 0; state_index < STATES.length; state_index++) {

        var state = STATES[state_index];

        // for each state, iterate each user
        for (var user_index = 0; user_index < users.length; user_index++) {

            var user = users[user_index];

			// The incident date is today's date minus random days (max -40)
			var gdt = new GlideDateTime();
			gdt.addDaysLocalTime(-1 * Math.floor(Math.random() * 40));
			gdt.addSeconds(-1 * Math.floor(Math.random() * 86400));
			incidentData['u_incident_datetime'] = gdt;

            insertIncident(incidentData, user.sys_id, user.primary_location, true, state);

            insertIncident(incidentData, user.sys_id, user.secondary_location, false, state);
        } // author
    } // state
} // inc
